// Import.
const crypto = require('crypto');

// Constants.
const HASH_ALGORITHM_SHA512 = 'sha512';
const HASH_DIGEST_HEX = 'hex';
const DEFAULT_DATA = '';
const DEFAULT_SALT = '';
const DEFAULT_ASYNC_INDICATOR = false;

/**
 * Hash data by SHA512 algorithm.
 * @param {string} data Data to hash.
 * @returns {string} Hash string.
 */
function sha512(data) {
  return crypto.createHash(HASH_ALGORITHM_SHA512).update(data).digest(HASH_DIGEST_HEX);
}

/**
 * Hash data using salt.
 * @param {string} data Data to hash.
 * @param {string} [salt] Salt.
 * @param {boolean} [isAsync] Is async indicator. Promise returned if this parameter defined as true.
 * @returns {string|Promise<string>} Salted SHA512 hash.
 */
function hash(data = DEFAULT_DATA, salt = DEFAULT_SALT, isAsync = DEFAULT_ASYNC_INDICATOR) {
  // Define salted data.
  const saltedData = data + salt;

  // Return hash promise if async indicator setted.
  if (isAsync) { return new Promise(resolve => resolve(sha512(saltedData))); }

  // Return hash in other cases.
  return sha512(saltedData);
}

// Export.
module.exports = hash;
